window.addEventListener("scroll",()=>{const e=document.querySelector(".sticky-header");window.scrollY>0?e.classList.add("scrolling"):e.classList.remove("scrolling")});const menuButton=document.querySelector("#menu-trigger"),menu=document.querySelector(".header-right"),menuIcon=menuButton.querySelector("i");menuButton.addEventListener("click",()=>{menu.classList.toggle("mobile-menu-open"),menuIcon.classList.toggle("fa-bars"),menuIcon.classList.toggle("fa-close")});